mapper.select:
select from com_org_form_rights a, com_org b, com_form c, com_form_rights d
b.org_id = a.org_id and c.form_id = a.form_id and d.form_id = a.form_id and d.right_id = a.right_id and a.ORG_ID = #{orgId,jdbcType=INTEGER}
and a.FORM_ID = #{formId,jdbcType=INTEGER}
and a.RIGHT_ID = #{rightId,jdbcType=VARCHAR}

mapper.delete:
delete from com_org_form_rights 
where 
FORM_ID = #{formId,jdbcType=INTEGER} 
and ORG_ID = #{orgId,jdbcType=INTEGER} 
 and RIGHT_ID = #{rightId,jdbcType=VARCHAR}
